  const toggle = useSelector((state)=>state.toggle.showCart)
  const cart = useSelector((state)=>state.cart);

  const fetchCart=async()=>{
    {const data = await axios.put(`https://my-project-d40ec-default-rtdb.firebaseio.com/
`);}

  }
  useEffect(()=>{
    fetchCart();
  },[cart]);


  return (
    <Layout>
     {toggle && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
